import Daugther from './Daugther';

class Father extends Component {
  render() {
    return (
      <div>
        <h3>Eu sou o pai!</h3>
        <MyContext.Consumer>
            {
                value => (
                    <div>
                      <p>{`Eu tenho R$ ${value.money} para gastar.`}</p>
                      <button onClick={value.spendMoney}>PEDIR UM IFOOD!</button> 

                    </div>
                )
            }
        </MyContext.Consumer>
        {/* <Daugther money={this.props.money} spendMoney={this.props.spendMoney}/> */}
      </div>
    )
  }
}

export default Father;
